import '../App.css';

function Productlist(props) {
    const Cart = (e)=>{
        if(sessionStorage.getItem('name')){
      e.preventDefault()
       callAPI('cart','POST',{
        name:names[e.target.accessKey].name,
        image:names[e.target.accessKey].image,
        price:names[e.target.accessKey].price,
        user: sessionStorage.getItem('name')
    })
            .then (res =>{
                console.log(res.data.cart)   
                })}
                else{
                    alert('Bạn phải đăng nhập mới có thể thêm sản phẩm vào giỏ hàng')
                }
    }
  return (
    <>
         <div className="" >
                    <div className="featured__item">
                        <div className="featured__item__pic set-bg">
                            <img src={props.image} />
                            <ul className="featured__item__pic__hover">
                                <li><a href="#"><i className="fa fa-shopping-cart" aria-hidden="true"  accessKey={props.index} onClick={e=>Cart(e)} ></i> </a></li>
                            </ul>
                        </div>
                        <div className="featured__item__text">
                            <h6><a href="#" >{props.name}</a></h6>
                            <h5>{props.price}VND</h5>
                        </div>
                    </div>
                </div>
    </>
  );
}

export default Productlist;
